import vscodenb # importing automatically registers the ipython magic
import multiprocess as mp

def work(cpus=mp.cpu_count()):
    def f(x):
        return sum(range(x * 100_000_000))
    with mp.Pool(cpus) as p:
        p.map(f, [1] * 2 * cpus)

